package loopDemo;

public class loopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String forsonuc = "For Döngüsü Bitti";
		String whilesonuc = "While Döngüsü Bitti";
		for (int i = 1; i <= 10; i++) {
			System.out.println(i);
		}
		System.out.println(forsonuc);

		
		
		int i = 1;
		while (i <= 10) {
			System.out.println(i);
			i++;
		}
		System.out.println(whilesonuc);

	}

}
